mapper.select:
select * from task


        and platform = #{platform}


        and task_type = #{taskType}


        and start_time > #{startTime}


        and end_time < #{endTime}


        and status = #{status}


order by start_time desc
limit #{startIndex}, #{pageSize}

select count(*) from task


        and platform = #{platform}


        and task_type = #{taskType}


        and start_time > #{startTime}


        and end_time < #{endTime}


        and status = #{status}
